userCSS:
alspotron-wrapper {
  opacity: 1 !important;
}

alspotron-nowplaying {
  width: calc(184px + 36px + 36px);
  padding: 6px;

  border-radius: 10px;
  border-top-left-radius: 0;
  border-top-right-radius: 0;
  background: black;
  opacity: 1 !important;
  overflow: hidden;

  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.2);

  transition:
    border-radius 0.6s cubic-bezier(0.2, 1.4, 0.5, 1),
    padding-top 0.6s cubic-bezier(0.2, 1.4, 0.5, 1),
    width 0.6s cubic-bezier(1, 0, 0.4, 1.2),
    box-shadow 0.6s cubic-bezier(0.2, 1.4, 0.5, 1);
}
alspotron-wrapper::before {
  content: '';
  position: absolute;
  top: 0;
  left: calc(50% - (184px + 36px + 36px) / 2 - 2px);
  width: 5px;
  height: 5px;
  background: black;
  translate: -50% 0;
  -webkit-mask-image: radial-gradient(circle 5px at 0 100%, transparent 0, transparent 5px, black 5px);

  transition: left 0.6s cubic-bezier(1, 0, 0.4, 1.2);
}
alspotron-wrapper::after {
  content: '';
  position: absolute;
  top: 0;
  right: calc(50% - (184px + 36px + 36px) / 2 - 7px);
  width: 5px;
  height: 5px;
  background: black;
  translate: -50% 0;
  -webkit-mask-image: radial-gradient(circle 5px at 100% 100%, transparent 0, transparent 5px, black 5px);

  transition: right 0.6s cubic-bezier(1, 0, 0.4, 1.2);
}

alspotron-nowplaying-progress-bar {
  width: 100%;
  height: 2px;

  inset: unset;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);

  overflow: hidden;
  z-index: -1;

  transition: height 0.6s cubic-bezier(0.16, 1, 0.3, 1);
}
alspotron-wrapper--stopped alspotron-nowplaying-progress-bar {
  height: 0 !important;
}
alspotron-nowplaying-progress {
}

alspotron-nowplaying-cover {
  width: 24px;
  height: 24px;

  border-radius: 4px;
  margin-right: calc(188px);

  transition:
    scale 0.6s cubic-bezier(0.16, 1, 0.3, 1),
    opacity 0.6s cubic-bezier(0.16, 1, 0.3, 1),
    border-radius 0.6s cubic-bezier(0.2, 1.4, 0.5, 1),
    height 0.6s cubic-bezier(0.2, 1.4, 0.5, 1),
    width 0.6s cubic-bezier(0.2, 1.4, 0.5, 1),
    margin-right 0.6s cubic-bezier(0.2, 1.4, 0.5, 1);
}

alspotron-wrapper--stopped:not(alspotron-wrapper--hover) alspotron-nowplaying-cover,
alspotron-wrapper--idle:not(alspotron-wrapper--hover) alspotron-nowplaying-cover {
  scale: 0.8;
  opacity: 0.5;
}

alspotron-nowplaying-container {
  justify-content: space-between;
}
alspotron-nowplaying-playing-text {
  transition: opacity 0.6s cubic-bezier(0.2, 1.4, 0.5, 1);
}
alspotron-wrapper--stopped:not(alspotron-wrapper--hover) alspotron-nowplaying-playing-text,
alspotron-wrapper--idle:not(alspotron-wrapper--hover) alspotron-nowplaying-playing-text {
  opacity: 0.5;
}
alspotron-nowplaying-marquee {
  width: 24px;
  height: 24px;

  justify-self: flex-end;

  transition: width 0.6s cubic-bezier(0.5, 1.4, 0.5, 1);
}

alspotron-lyrics {
  flex-direction: column !important;
  opacity: var(--proximity);
  transition: opacity 1.6s cubic-bezier(0.2, 1.4, 0.5, 1);
}

alspotron-lyrics-item {
  border-radius: 4px;
  --reverse-lyric-order: calc(2 - (var(--lyric-order) - var(--lyric-order-offset, 0)) + var(--lyric-order-offset, 0));
  --transition-delay: calc(var(--reverse-lyric-order) * 75ms) !important;
}

alspotron-nowplaying-artist {
  transition: font-size 0.6s cubic-bezier(0.2, 1.4, 0.5, 1);
}
alspotron-nowplaying-divider {
  transition: font-size 0.6s cubic-bezier(0.2, 1.4, 0.5, 1);
}
alspotron-nowplaying-title {
  transition: font-size 0.6s cubic-bezier(0.2, 1.4, 0.5, 1);
}

/* hover */
alspotron-wrapper--hover::before {
  left: calc(50% - (184px + 4px) / 2 - 2.5px);
}
alspotron-wrapper--hover::after {
  right: calc(50% - (184px + 4px) / 2 - 7.5px);
}

alspotron-wrapper--hover alspotron-nowplaying {
  width: calc(184px + 4px);
  padding-top: calc(34px + 8px);

  border-radius: 24px;
  border-top-left-radius: 0;
  border-top-right-radius: 0;

  box-shadow: 0 6px 16px rgba(0, 0, 0, 0.5);
}
alspotron-wrapper--hover alspotron-nowplaying-cover {
  width: 64px;
  height: 64px;
  margin-right: 4px;
  border-radius: 4px;
  border-bottom-left-radius: 20px;

  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.2);
}

alspotron-wrapper--hover alspotron-nowplaying-marquee {
  width: 100%;
}
alspotron-wrapper--hover alspotron-nowplaying-progress-bar {
  height: calc(100% - 34px);
  border-radius: 5px;
  border-left-bottom-radius: 0px;
  border-right-bottom-radius: 0px;
}

alspotron-wrapper--hover alspotron-nowplaying-artist {
  font-size: 20px !important;
}
alspotron-wrapper--hover alspotron-nowplaying-divider {
  font-size: 20px !important;
}
alspotron-wrapper--hover alspotron-nowplaying-title {
  font-size: 20px !important;
}

/* custom-animation */

alspotron-transition-lyric-enter {
  transform: translateY(-100%);
  opacity: 0;
}

alspotron-transition-lyric-exit-to {
  transform: scale(120%);
  opacity: 0;
  filter: blur(4px);
}

alspotron-transition-lyric-move {
  transition: all 0.625s ease-in-out;
  transition-delay: var(--transition-delay, 0s);
  transform-origin: center;
}

alspotron-transition-lyric-enter-active {
  transition: all 0.3s cubic-bezier(0.25, 1, 0.5, 1);
  transition-delay: var(--transition-delay, 0s);
  transform-origin: center;

}
alspotron-transition-lyric-exit-active {
  position: absolute;
  transition: all 0.3s cubic-bezier(0.5, 0, 0.75, 0);
  transition-delay: var(--transition-delay, 0s);
  transform-origin: center;
}
