userCSS:
alspotron-wrapper {
  opacity: 1 !important;
}

alspotron-nowplaying {
  width: calc(184px + 36px + 36px);
  padding: 6px;

  border-radius: 8px;
  border-top-left-radius: 0;
  border-top-right-radius: 0;
  background: black;
  opacity: 1 !important;

  transition:
    padding-top 0.6s cubic-bezier(0.16, 1, 0.3, 1),
    width 0.6s cubic-bezier(0.16, 1, 0.3, 1),
    box-shadow 0.6s cubic-bezier(0.16, 1, 0.3, 1);
}

alspotron-nowplaying-progress-bar {
  width: 100%;
  height: 2px;

  inset: unset;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);

  overflow: hidden;
  z-index: -1;

  transition: height 0.6s cubic-bezier(0.16, 1, 0.3, 1);
}
alspotron-wrapper--stopped alspotron-nowplaying-progress-bar {
  height: 0 !important;
}
alspotron-nowplaying-progress {
}

alspotron-nowplaying-cover {
  width: 24px;
  height: 24px;

  margin-right: calc(188px);
  height: 100%;

  background: blue;

  transition: all 0.6s cubic-bezier(0.16, 1, 0.3, 1);
}

alspotron-wrapper--stopped alspotron-nowplaying-cover {
  scale: 0.8;
  opacity: 0.5;
}

alspotron-nowplaying-marquee {
  width: 24px;
  height: 24px;

  transition: width 0.6s cubic-bezier(0.16, 1, 0.3, 1);
}

alspotron-lyrics {
  flex-direction: column !important;
  opacity: var(--proximity);
  transition: opacity 1.2s cubic-bezier(0.16, 1, 0.3, 1);
}

alspotron-lyrics-item {
  border-radius: 4px;
  --reverse-lyric-order: calc(2 - (var(--lyric-order) - var(--lyric-order-offset, 0)) + var(--lyric-order-offset, 0));
  --transition-delay: calc(var(--reverse-lyric-order) * 75ms) !important;
}

alspotron-nowplaying-artist {
  transition: font-size 0.6s cubic-bezier(0.16, 1, 0.3, 1);
}
alspotron-nowplaying-divider {
  transition: font-size 0.6s cubic-bezier(0.16, 1, 0.3, 1);
}
alspotron-nowplaying-title {
  transition: font-size 0.6s cubic-bezier(0.16, 1, 0.3, 1);
}

/* hover */

alspotron-wrapper--hover alspotron-nowplaying {
  width: calc(184px);
  padding-top: calc(34px + 8px);

  box-shadow: 0 6px 16px rgba(0, 0, 0, 0.5);
}
alspotron-wrapper--hover alspotron-nowplaying-cover {
  width: 64px;
  height: 64px;
  margin-right: 4px;
  border-radius: 4px;

  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.2);
}
alspotron-wrapper--hover alspotron-nowplaying-marquee {
  width: 100%;
}
alspotron-wrapper--hover alspotron-nowplaying-progress-bar {
  height: 100%;
}

alspotron-wrapper--hover alspotron-nowplaying-artist {
  font-size: 20px !important;
}
alspotron-wrapper--hover alspotron-nowplaying-divider {
  font-size: 20px !important;
}
alspotron-wrapper--hover alspotron-nowplaying-title {
  font-size: 20px !important;
}

/* custom-animation */

alspotron-transition-lyric-enter {
  transform: translateY(-100%);
  opacity: 0;
}

alspotron-transition-lyric-exit-to {
  transform: scale(120%);
  opacity: 0;
  filter: blur(4px);
}

alspotron-transition-lyric-move {
  transition: all 0.625s ease-in-out;
  transition-delay: var(--transition-delay, 0s);
  transform-origin: center;
}

alspotron-transition-lyric-enter-active {
  transition: all 0.3s cubic-bezier(0.25, 1, 0.5, 1);
  transition-delay: var(--transition-delay, 0s);
  transform-origin: center;

}
alspotron-transition-lyric-exit-active {
  position: absolute;
  transition: all 0.3s cubic-bezier(0.5, 0, 0.75, 0);
  transition-delay: var(--transition-delay, 0s);
  transform-origin: center;
}
