userCSS:
alspotron-wrapper {
  min-height: 100%;
  align-items: end;
}

alspotron-lyrics-container {
  width: fit-content;
  padding: 7.890625vw 2.8125vw;
  transform: skew(0, 3.8deg);
  align-items: flex-end;
}

alspotron-lyrics-wrapper {
  transform-origin: 100% 50%;
}
alspotron-lyrics {
  align-items: flex-end;
}

alspotron-lyrics-item {
  border-radius: 8px;
  border-top-right-radius: 0;
  border-bottom-right-radius: 0;
  padding: 4px 8px;

  box-shadow: -6px 0 0 -2px rgb(46, 216, 255, 0.5) inset, 0 0 4px rgba(0, 0, 0, 0.5);
}

alspotron-nowplaying {
  position: absolute;
  left: 50%;
  right: 50%;
  bottom: 4.140625vw;

  width: 7.421875%;
  aspect-ratio: 1;

  border-radius: 50%;
  flex-shrink: 0;

  display: inline-flex;
  flex-flow: column;
  justify-content: flex-end;
  align-items: flex-start;

  opacity: 0.5;
  padding: 0;

  translate: -50% 0;
  animation: rotate linear 10s infinite;

  mask: radial-gradient(transparent 10%, black 50%);
}
alspotron-nowplaying-progress-bar {
}

alspotron-nowplaying-progress {
  --color: #ff8a17;

  width: 100%;
  height: 100%;

  border-radius: 100%;
  transform: unset !important;
  transform-origin: 50% 50%;
  background: conic-gradient(
    var(--color) 0%,
    var(--color) var(--nowplaying-percent),
    transparent var(--nowplaying-percent)
  );
  opacity: 1;
  mix-blend-mode: multiply;

  animation: rotate-reverse linear 10s infinite;
}
alspotron-nowplaying-cover {
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  z-index: -1;

  object-fit: cover;
  border-radius: 100%;
}

alspotron-wrapper--stopped alspotron-nowplaying-cover {
}

alspotron-nowplaying-playing-text {
  position: relative;
  z-index: 50;
  margin-top: auto;
  padding: 12px 16px;
  overflow: visible !important;

  text-shadow: 0 0 2px black, 0 3px 6px black;
  transform: translateZ(100px);
}

alspotron-marquee {
  z-index: 50;
}

alspotron-nowplaying-marquee {
  display: none;
}

@keyframes rotate {
  0% {
    rotate: 0deg;
  }
  100% {
    rotate: 360deg;
  }
}

@keyframes rotate-reverse {
  0% {
    rotate: 360deg;
  }
  100% {
    rotate: 0deg;
  }
}
